import React, { useState, useEffect } from "react";
import axios from 'axios';
import { useHistory } from "react-router-dom";
import { LoginSchema } from '../schemas/schemas';
import { Form, Formik, Field, ErrorMessage, FieldArray } from "formik";

const initialFormData = { firstName: "", friends: [] };
export default function Result() {
    const [courses, setCourses] = useState(initialFormData);

    useEffect(() => {

        axios.get('/api/courses',)
            .then((x) => {
                setCourses(x.data);
            })
            .catch((err) => {
                // toast.error(err.response.data, {
                //     position: "top-right",
                //     autoClose: 5000,
                //     hideProgressBar: false
                // });

            })

    }, []);
    return (
        <div className="container">
            <div className="row">
                <div className="col-md-6">
                    <h1 className="text-dark">Result</h1>
                    <p>Please fill in this form to set your result!</p>
                    <Formik
                        initialValues={{ email: '', password: '' }}
                        validationSchema={LoginSchema}
                        onSubmit={(values, actions) => {
                            console.log('hi');
                            actions.setSubmitting(false);
                        }}
                    >
                        {props => (
                            <Form onSubmit={props.handleSubmit}>
                                <Field className="form-control" name="role" as="select">
                                    <option value="" disabled selected>--Select Course--</option>
                                    {courses && courses.map(x => (
                                        <option value={x.id}>{x.id}</option>
                                    ))}
                                </Field>
                                <div>

                                </div>
                                <Field type="text" className="form-control" name="gpa" placeholder="GPA" />
                                <div className="invalid-feedback">
                                    <ErrorMessage
                                        name="gpa"
                                    />
                                </div>

                                <button type="submit" className="btn btn-dark text-white">Submit</button>
                            </Form>
                        )}
                    </Formik>
                </div>
                <div className="col-md-6">

                </div>
            </div>
        </div>
    )
}
